CREATE TABLE hib_departamento (id UUID NOT NULL PRIMARY KEY, nombre varchar(50) NOT NULL UNIQUE, jefe UUID DEFAULT NULL);
CREATE TABLE hib_empleado (id UUID NOT NULL PRIMARY KEY, nombre varchar(100) NOT NULL, salario decimal(10,2) DEFAULT NULL, nacimiento varchar(100) DEFAULT NULL,departamento UUID DEFAULT NULL);
CREATE TABLE hib_proyecto (id UUID NOT NULL PRIMARY KEY, nombre varchar(100) NOT NULL);
CREATE TABLE hib_empleado_hib_proyecto (empleado_id BINARY(16) NOT NULL, proyecto_id BINARY(16) NOT NULL,PRIMARY KEY (empleado_id, proyecto_id),FOREIGN KEY (empleado_id) REFERENCES hib_empleado (id),FOREIGN KEY (proyecto_id) REFERENCES hib_proyecto (id));